import { useControlsContext } from '../context.mjs';

const useAddFolder = () => {
    const { data: { addFolderLabel, isAddFolderDisabled }, onAddFolder, } = useControlsContext();
    return {
        isDisabled: isAddFolderDisabled,
        label: addFolderLabel,
        onAddFolder,
    };
};

export { useAddFolder };
